---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;

const contentWrapperVariants = cva(
  "grid grid-rows-[0fr] transition-[grid-template-rows] duration-150 ease-in-out group-data-[state=open]/accordion:grid-rows-[1fr]",
);

const contentVariants = cva("overflow-hidden min-h-0 [&[hidden]]:!block");

const contentInnerVariants = cva("pt-0 pb-4");
---

<div class={cn("group/accordion", rest.class)} {...rest}>
  <div class={cn(contentWrapperVariants())}>
    <div data-slot="accordion-content" class={cn(contentVariants())}>
      <div class={cn(contentInnerVariants(), className)}>
        <slot />
      </div>
    </div>
  </div>
</div>

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  value: string;
  open?: boolean;
  class?: string;
} & HTMLAttributes<"details">;

const {
  value,
  open = false,
  class: className = "",
  ...rest
} = Astro.props as Props;

const itemVariants = cva("border-b last:border-b-0");
---

<div
  {...rest}
  data-value={value}
  data-slot="accordion-item"
  class={cn(itemVariants(), "group/accordion", className)}
>
  <slot />
</div>

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
} & HTMLAttributes<"summary">;

const { class: className = "", ...rest } = Astro.props as Props;

const triggerVariants = cva(
  "focus-visible:border-ring w-full focus-visible:ring-ring/50 flex flex-1 items-start justify-between gap-4 rounded-md py-4 text-left text-sm font-medium transition-all outline-none hover:underline focus-visible:ring-[3px] disabled:pointer-events-none disabled:opacity-50 [&[aria-expanded=true]>svg]:rotate-180",
);
---

<button
  {...rest}
  data-slot="accordion-trigger"
  class={cn(triggerVariants(), className)}
>
  <span class="flex-1">
    <slot />
  </span>
  {
    Astro.slots.has("icon") ? (
      <span class="inline-flex items-center justify-center transition-transform duration-200 group-open/accordion:rotate-180">
        <slot name="icon" />
      </span>
    ) : (
      <svg
        class="size-4 shrink-0 text-muted-foreground translate-y-0.5 transition-transform duration-200 group-open/accordion:rotate-180"
        viewBox="0 0 24 24"
        fill="none"
        stroke="currentColor"
        stroke-width="2"
        stroke-linecap="round"
        stroke-linejoin="round"
        aria-hidden="true"
      >
        <path d="m6 9 6 6 6-6" />
      </svg>
    )
  }
</button>

export { default as Accordion } from "./Accordion.astro";
export { default as AccordionItem } from "./AccordionItem.astro";
export { default as AccordionTrigger } from "./AccordionTrigger.astro";
export { default as AccordionContent } from "./AccordionContent.astro";
