---
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<Tag
  {...rest}
  data-slot="avatar-image"
  class={cn("relative z-10 aspect-square size-full [&_img]:size-full [&_img]:object-cover", className)}
>
  <slot />
</Tag>

---
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<Tag
  {...rest}
  data-slot="avatar-fallback"
  class={cn("absolute inset-0 bg-muted flex size-full items-center justify-center rounded-full text-muted-foreground font-medium uppercase select-none", className)}
>
  <slot />
</Tag>

export { default as Avatar } from "./Avatar.astro";
export { default as AvatarImage } from "./AvatarImage.astro";
export { default as AvatarFallback } from "./AvatarFallback.astro";
