---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"li">;

const {
  as: Tag = "li",
  class: className = "",
  ...rest
} = Astro.props as Props;

const separatorVariants = cva("[&>svg]:size-3.5");
---

<Tag
  {...rest}
  role="presentation"
  aria-hidden="true"
  data-slot="breadcrumb-separator"
  class={cn(separatorVariants(), className)}
>
  <slot>
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      stroke-width="2"
      stroke-linecap="round"
      stroke-linejoin="round"
      class="size-3.5"
      aria-hidden="true"
    >
      <path d="m9 18 6-6-6-6" />
    </svg>
  </slot>
</Tag>

export { default as Breadcrumb } from "./Breadcrumb.astro";
export { default as BreadcrumbList } from "./BreadcrumbList.astro";
export { default as BreadcrumbItem } from "./BreadcrumbItem.astro";
export { default as BreadcrumbSeparator } from "./BreadcrumbSeparator.astro";
