---
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type CardOverlayVariant = "default" | "glass";

type Props = {
  variant?: CardOverlayVariant;
  class?: string;
} & HTMLAttributes<"div">;

const {
  class: className = "",
  variant = "default",
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  data-slot="card-overlay"
  class={cn(
    "inset-0 absolute z-10 opacity-0 p-6 group-hover/card:opacity-100 transition-opacity duration-300 ease-out",
    variant === "glass"
      ? "bg-primary/70 backdrop-blur-sm text-primary-foreground"
      : "bg-primary text-primary-foreground",
    className,
  )}
>
  <slot />
</div>

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;

const titleVariants = cva("leading-none font-medium text-xl text-balance");
---

<div {...rest} data-slot="card-title" class={cn(titleVariants(), className)}>
  <slot />
</div>

export { default as Card } from "./Card.astro";
export { default as CardMedia } from "./CardMedia.astro";
export { default as CardHeader } from "./CardHeader.astro";
export { default as CardTitle } from "./CardTitle.astro";
export { default as CardDescription } from "./CardDescription.astro";
export { default as CardAction } from "./CardAction.astro";
export { default as CardContent } from "./CardContent.astro";
export { default as CardFooter } from "./CardFooter.astro";
export { default as CardOverlay } from "./CardOverlay.astro";
