---
/**
 * @component Carousel
 * @title Carousel
 * @description A CSS scroll-snap carousel using only Tailwind classes (no JS).
 * @status stable
 *
 * @example
 *
 * <!-- Horizontal images with center snapping -->
 * <Carousel align="center" class="gap-4 p-2">
 *   <img src="/demo/5da428d9994d1e1aa4dbc7149569a331e2b4cc0c.png" alt="Example" class="w-64 h-40 object-cover rounded-lg shrink-0" />
 *   <img src="/demo/ce1eef98818713195a5a8fbd1428cd3a9ba2baa9.png" alt="Example" class="w-64 h-40 object-cover rounded-lg shrink-0" />
 *   <img src="/demo/dcaf6fd7d219ef3692f5699c125016f5f62d1d48.png" alt="Example" class="w-64 h-40 object-cover rounded-lg shrink-0" />
 * </Carousel>
 *
 * @usage
 *
 * ```astro
 * ---
 * import Carousel from '@/components/ui/Carousel.astro';
 * ---
 *
 * <Carousel>
 *   <!-- Your items as direct children; give them widths (e.g., w-64) and set shrink-0 -->
 *   <div class="w-72 shrink-0 bg-muted rounded-xl p-6">Item 1</div>
 *   <div class="w-72 shrink-0 bg-muted rounded-xl p-6">Item 2</div>
 *   <div class="w-72 shrink-0 bg-muted rounded-xl p-6">Item 3</div>
 * </Carousel>
 * ```
 *
 * @props
 * - axis?: "x" | "y" — Scroll direction. Default: "x".
 * - snapType?: "mandatory" | "proximity" — Scroll-snap behavior. Default: "mandatory".
 * - align?: "start" | "center" | "end" — Child snap alignment. Default: "start".
 * - class?: string — Additional classes for the container.
 *
 * @examples
 *
 * ### Vertical list, proximity snapping
 *
 * <Carousel axis="y" snapType="proximity" class="h-64 gap-3 p-3">
 *   <Card class="w-full shrink-0"> <Fragment slot="content">A</Fragment> </Card>
 *   <Card class="w-full shrink-0"> <Fragment slot="content">B</Fragment> </Card>
 *   <Card class="w-full shrink-0"> <Fragment slot="content">C</Fragment> </Card>
 * </Carousel>
 */

import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";

interface Props {
  axis?: "x" | "y";
  snapType?: "mandatory" | "proximity";
  align?: "start" | "center" | "end";
  class?: string;
  props?: Astro.ComponentProps<"div">;
}

const {
  axis = "x",
  snapType = "mandatory",
  align = "start",
  class: className = "",
  ...props
} = Astro.props as Props;

const carouselVariants = cva(
  // Base container: overflow scroll, hide scrollbar, smooth scroll on supported browsers
  "relative w-full overflow-auto scroll-smooth [scrollbar-width:none] [-ms-overflow-style:none] [&::-webkit-scrollbar]:hidden",
  {
    variants: {
      axis: {
        x: "flex flex-row snap-x",
        y: "flex flex-col snap-y",
      },
      snapType: {
        mandatory: "snap-mandatory",
        proximity: "snap-proximity",
      },
      align: {
        start: "[&>*]:snap-start [&>*]:shrink-0",
        center: "[&>*]:snap-center [&>*]:shrink-0",
        end: "[&>*]:snap-end [&>*]:shrink-0",
      },
    },
    defaultVariants: {
      axis: "x",
      snapType: "mandatory",
      align: "start",
    },
  },
);
---

<div class={cn(carouselVariants({ axis, snapType, align }), className)} {...props}>
  <slot />
</div>
