---
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
  /** Radix-like: render wrapper so the child can be the interactive element */
  asChild?: boolean;
} & HTMLAttributes<"button">;

const {
  class: className = "",
  asChild = false,
  type = "button",
  ...rest
} = Astro.props as Props;
---

{
  asChild ? (
    <div data-slot="collapsible-trigger" data-as-child class={cn(className)}>
      <slot />
    </div>
  ) : (
    <button
      {...rest}
      type={type}
      data-slot="collapsible-trigger"
      class={cn(className)}
    >
      <slot />
    </button>
  )
}

---
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
} & VariantProps<typeof collapsibleContentVariants> &
  HTMLAttributes<"div">;

export const collapsibleContentVariants = cva("overflow-hidden", {
  variants: {
    animation: {
      none: "",
      slide:
        "h-(--collapsible-panel-height) transition-[height] duration-200 ease-out motion-reduce:transition-none",
    },
  },
  defaultVariants: {
    animation: "none",
  },
});

const {
  class: className = "",
  animation = "none",
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  data-slot="collapsible-content"
  class={cn(collapsibleContentVariants({ animation }), className)}
>
  <slot />
</div>

export { default as Collapsible } from "./Collapsible.astro";
export { default as CollapsibleTrigger } from "./CollapsibleTrigger.astro";
export { default as CollapsibleContent } from "./CollapsibleContent.astro";
export { collapsibleContentVariants } from "./CollapsibleContent.astro";
