---
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
  /** Radix-like: render wrapper so the child can be the interactive element */
  asChild?: boolean;
} & HTMLAttributes<"button">;

const {
  class: className = "",
  asChild = false,
  type = "button",
  ...rest
} = Astro.props as Props;
---

{
  asChild ? (
    <div data-slot="collapsible-trigger" data-as-child class={cn(className)}>
      <slot />
    </div>
  ) : (
    <button
      {...rest}
      type={type}
      data-slot="collapsible-trigger"
      class={cn(className)}
    >
      <slot />
    </button>
  )
}

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;

const contentWrapperVariants = cva(
  "grid grid-rows-[0fr] transition-[grid-template-rows] duration-150 ease-in-out group-data-[state=open]/collapsible:grid-rows-[1fr]",
);

const contentVariants = cva("overflow-hidden min-h-0");
---

<div class={cn(contentWrapperVariants())}>
  <div {...rest} data-slot="collapsible-content" class={cn(contentVariants(), className)}>
    <slot />
  </div>
</div>

export { default as Collapsible } from "./Collapsible.astro";
export { default as CollapsibleTrigger } from "./CollapsibleTrigger.astro";
export { default as CollapsibleContent } from "./CollapsibleContent.astro";
