---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  align?: "start" | "center" | "end";
  sideOffset?: number;
  class?: string;
} & HTMLAttributes<"div">;

const {
  align = "start",
  sideOffset = 4,
  class: className = "",
  ...rest
} = Astro.props as Props;

const alignClasses = {
  start: "left-0",
  center: "left-1/2 -translate-x-1/2",
  end: "right-0",
};
---

<div
  {...rest}
  data-slot="dropdown-menu-content"
  data-align={align}
  style={`--side-offset: ${sideOffset}px`}
  class={cn(
    "absolute z-50 min-w-[8rem] overflow-hidden rounded-md border border-border bg-popover p-1 text-popover-foreground shadow-md",
    "top-full mt-[var(--side-offset)]",
    alignClasses[align],
    "hidden",
    "data-[state=open]:block",
    // "opacity-0 scale-95 pointer-events-none",
    "data-[state=open]:opacity-100 data-[state=open]:scale-100 data-[state=open]:pointer-events-auto",
    "starting:opacity-0 starting:scale-97 starting:pointer-events-none transition-discrete transition-[opacity,scale,transform,visibility] duration-500 ease-out",
    className,
  )}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<div
  {...rest}
  role="group"
  data-slot="dropdown-menu-group"
  class={cn("", className)}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  disabled?: boolean;
  inset?: boolean;
  class?: string;
} & HTMLAttributes<"div">;

const {
  disabled = false,
  inset = false,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  role="menuitem"
  tabindex={disabled ? -1 : 0}
  data-slot="dropdown-menu-item"
  data-disabled={disabled ? "" : undefined}
  class={cn(
    "relative flex cursor-default select-none items-center gap-2 rounded-sm px-2 py-1.5 text-sm outline-none",
    "data-highlighted:bg-accent data-highlighted:text-accent-foreground",
    "data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
    "group/dropdown-menu-item",
    inset && "pl-8",
    className,
  )}
>
  <slot />
</div>

