---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";
import { cva } from "class-variance-authority";

type Props = {
  align?: "start" | "center" | "end";
  alignOffset?: number;
  side?: "top" | "bottom" | "left" | "right";
  sideOffset?: number;
  class?: string;
} & HTMLAttributes<"div">;

const {
  align = "start",
  alignOffset = 0,
  side = "bottom",
  sideOffset = 4,
  class: className = "",
  ...rest
} = Astro.props as Props;

const contentVariants = cva([
  "z-50 hidden data-[state=open]:block [transition:display_150ms_allow-discrete] starting:data-[state=closed]:block outline-hidden",
  "min-w-32 overflow-hidden rounded-md border border-border bg-popover p-1 text-popover-foreground shadow-md",
  "data-[state=open]:animate-in data-[state=closed]:animate-out",
  "data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
  "data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95",
  "data-[side=bottom]:slide-in-from-top-2 data-[side=top]:slide-in-from-bottom-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2",
  "origin-(--transform-origin) [--tw-animation-fill-mode:both]",
]);
---

<div
  {...rest}
  data-slot="dropdown-menu-content"
  data-align={align}
  data-align-offset={alignOffset}
  data-side={side}
  data-side-offset={sideOffset}
  class={cn(contentVariants(), className)}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<div
  {...rest}
  role="group"
  data-slot="dropdown-menu-group"
  class={cn("", className)}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  disabled?: boolean;
  inset?: boolean;
  class?: string;
} & HTMLAttributes<"div">;

const {
  disabled = false,
  inset = false,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  role="menuitem"
  tabindex={disabled ? -1 : 0}
  data-slot="dropdown-menu-item"
  data-disabled={disabled ? "" : undefined}
  class={cn(
    "relative flex cursor-default select-none items-center gap-2 rounded-sm px-2 py-1.5 text-sm outline-none",
    "data-highlighted:bg-accent data-highlighted:text-accent-foreground",
    "data-[disabled]:pointer-events-none data-[disabled]:opacity-50",
    "group/dropdown-menu-item",
    inset && "pl-8",
    className,
  )}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  inset?: boolean;
  class?: string;
} & HTMLAttributes<"div">;

const { inset = false, class: className = "", ...rest } = Astro.props as Props;
---

<div
  {...rest}
  data-slot="dropdown-menu-label"
  class={cn("px-2 py-1.5 text-sm font-medium", inset && "pl-8", className)}
>
  <slot />
</div>

