---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"span">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<span
  {...rest}
  data-slot="dropdown-menu-shortcut"
  class={cn(
    "ml-auto text-xs tracking-widest text-muted-foreground ",
    "group-data-highlighted/dropdown-menu-item:text-accent-foreground/70",
    className,
  )}
>
  <slot />
</span>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";
import { Button } from "../button";

type Props = {
  class?: string;
  /** Radix-like: render wrapper so the child can be the interactive element */
  asChild?: boolean;
} & HTMLAttributes<"button">;

const { class: className = "", asChild = false, ...rest } = Astro.props as Props;
---

{
  asChild ? (
    <div data-slot="dropdown-menu-trigger" data-as-child class={cn("inline-flex items-center", className)}>
      <slot />
    </div>
  ) : (
    <Button
      data-slot="dropdown-menu-trigger"
      class={cn("inline-flex items-center", className)}
      {...rest as any}
    >
      <slot />
    </Button>
  )
}

export { default as DropdownMenu } from "./DropdownMenu.astro";
export { default as DropdownMenuTrigger } from "./DropdownMenuTrigger.astro";
export { default as DropdownMenuContent } from "./DropdownMenuContent.astro";
export { default as DropdownMenuGroup } from "./DropdownMenuGroup.astro";
export { default as DropdownMenuLabel } from "./DropdownMenuLabel.astro";
export { default as DropdownMenuItem } from "./DropdownMenuItem.astro";
export { default as DropdownMenuSeparator } from "./DropdownMenuSeparator.astro";
export { default as DropdownMenuShortcut } from "./DropdownMenuShortcut.astro";
