---
/**
 * @component InputGroup
 * @title Input Group
 * @description An input field with attached addons like icons, buttons, or text labels.
 * @figmaUrl https://www.figma.com/design/koxai7zw5vIuBzuVxe5T2l/bejamas-ui?node-id=2549-20918&t=YFStJ3V8fXEO8QD8-4
 *
 * @preview
 *
 * <div class="grid w-full max-w-sm gap-6">
 *   <InputGroup>
 *     <InputGroupInput placeholder="Search..." />
 *     <InputGroupAddon>
 *       <SearchIcon />
 *     </InputGroupAddon>
 *     <InputGroupAddon align="inline-end">12 results</InputGroupAddon>
 *   </InputGroup>
 *
 *   <InputGroup>
 *     <InputGroupInput placeholder="example.com" class="!pl-1" />
 *     <InputGroupAddon>
 *       <InputGroupText>https://</InputGroupText>
 *     </InputGroupAddon>
 *     <InputGroupAddon align="inline-end">
 *       <InputGroupButton size="xs">Check</InputGroupButton>
 *     </InputGroupAddon>
 *   </InputGroup>
 *
 *   <InputGroup>
 *     <InputGroupTextarea placeholder="Ask, Search or Chat..." />
 *     <InputGroupAddon align="block-end">
 *       <InputGroupButton size="icon-xs" class="rounded-full">
 *         <PlusIcon />
 *       </InputGroupButton>
 *       <InputGroupButton variant="ghost">Auto</InputGroupButton>
 *       <InputGroupText class="ml-auto">52% used</InputGroupText>
 *       <Separator orientation="vertical" class="!h-4" />
 *       <InputGroupButton size="icon-xs" variant="default" class="rounded-full">
 *         <ArrowUpIcon />
 *         <span class="sr-only">Send</span>
 *       </InputGroupButton>
 *     </InputGroupAddon>
 *   </InputGroup>
 * </div>
 *
 * @usage
 *
 * ```astro
 * ---
 * import { InputGroup, InputGroupInput, InputGroupAddon, InputGroupButton, InputGroupText } from '@bejamas/ui/components/input-group';
 * ---
 *
 * <InputGroup>
 *   <InputGroupInput placeholder="Search..." />
 *   <InputGroupAddon><SearchIcon /></InputGroupAddon>
 * </InputGroup>
 * ```
 */
import type { HTMLAttributes, HTMLTag } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  class: className = "",
  ...rest
} = Astro.props as Props;

const rootClasses =
  "group/input-group border-input dark:bg-input/30 relative flex w-full items-center rounded-md border shadow-xs transition-[color,box-shadow] outline-none h-9 min-w-0 has-[>textarea]:h-auto has-[>[data-align=inline-start]]:[&>input]:pl-2 has-[>[data-align=inline-end]]:[&>input]:pr-2 has-[>[data-align=block-start]]:h-auto has-[>[data-align=block-start]]:flex-col has-[>[data-align=block-start]]:[&>input]:pb-3 has-[>[data-align=block-end]]:h-auto has-[>[data-align=block-end]]:flex-col has-[>[data-align=block-end]]:[&>input]:pt-3 has-[[data-slot=input-group-control]:focus-visible]:border-ring has-[[data-slot=input-group-control]:focus-visible]:ring-ring/50 has-[[data-slot=input-group-control]:focus-visible]:ring-[3px] has-[[data-slot][aria-invalid=true]]:ring-destructive/20 has-[[data-slot][aria-invalid=true]]:border-destructive dark:has-[[data-slot][aria-invalid=true]]:ring-destructive/40";
---

<Tag
  {...rest}
  role="group"
  data-slot="input-group"
  class={cn(rootClasses, className)}
>
  <slot />
</Tag>

<script type="module">
  // Focus the nearest input when clicking on addons (unless clicking a button)
  document.addEventListener("click", (e) => {
    const addon = e.target.closest("[data-slot='input-group-addon']");
    if (!addon) return;
    if (e.target.closest("button")) return;
    const root = addon.closest("[data-slot='input-group']");
    if (!root) return;
    const input = root.querySelector("[data-slot='input-group-control']");
    input?.focus();
  });
  // Disabled styling passthrough when any control is disabled
  document.querySelectorAll("[data-slot='input-group']").forEach((group) => {
    const updateDisabled = () => {
      const ctrl = group.querySelector("[data-slot='input-group-control']");
      group.dataset.disabled = ctrl?.hasAttribute("disabled")
        ? "true"
        : "false";
    };
    updateDisabled();
    group.addEventListener("change", updateDisabled, { capture: true });
  });
</script>

---
import type { HTMLAttributes, HTMLTag } from "astro/types";
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";

type Align = "inline-start" | "inline-end" | "block-start" | "block-end";

type Props = {
  as?: HTMLTag;
  align?: Align;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  align = "inline-start",
  class: className = "",
  ...rest
} = Astro.props as Props;

const addonVariants = cva(
  "text-muted-foreground flex h-auto cursor-text items-center justify-center gap-2 py-1.5 text-sm font-medium select-none [&>svg:not([class*='size-'])]:size-4 [&>kbd]:rounded-[calc(var(--radius)-5px)] group-data-[disabled=true]/input-group:opacity-50",
  {
    variants: {
      align: {
        "inline-start":
          "order-first pl-3 has-[>button]:ml-[-0.45rem] has-[>kbd]:ml-[-0.35rem]",
        "inline-end":
          "order-last pr-3 has-[>button]:mr-[-0.45rem] has-[>kbd]:mr-[-0.35rem]",
        "block-start":
          "order-first w-full justify-start px-3 pt-3 [.border-b]:pb-3 group-has-[>input]/input-group:pt-2.5",
        "block-end":
          "order-last w-full justify-start px-3 pb-3 [.border-t]:pt-3 group-has-[>input]/input-group:pb-2.5",
      },
    },
    defaultVariants: {
      align: "inline-start",
    },
  },
);
---

<Tag
  {...rest}
  role="group"
  data-slot="input-group-addon"
  data-align={align}
  class={cn(addonVariants({ align }), className)}
>
  <slot />
</Tag>

---
import type { HTMLAttributes, HTMLTag } from "astro/types";
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import { Button } from "../button";

type GroupButtonSize = "xs" | "sm" | "icon-xs" | "icon-sm";

type Props = {
  as?: HTMLTag;
  size?: GroupButtonSize;
  variant?: "default" | "secondary" | "outline" | "ghost" | "destructive" | "link";
  type?: "button" | "submit" | "reset";
  class?: string;
} & HTMLAttributes<"button">;

const {
  as: rawTag,
  size = "xs",
  variant = "ghost",
  type = "button",
  class: className = "",
  ...rest
} = Astro.props as Props;

const buttonSizeVariants = cva("text-sm shadow-none flex gap-2 items-center", {
  variants: {
    size: {
      xs: "h-6 gap-1 px-2 rounded-[calc(var(--radius)-5px)] [&>svg:not([class*='size-'])]:size-3.5 has-[>svg]:px-2",
      sm: "h-8 px-2.5 gap-1.5 rounded-md has-[>svg]:px-2.5",
      "icon-xs": "size-6 rounded-[calc(var(--radius)-5px)] p-0 has-[>svg]:p-0",
      "icon-sm": "size-8 p-0 has-[>svg]:p-0",
    },
  },
  defaultVariants: {
    size: "xs",
  },
});

const Tag = rawTag ?? undefined;
---

{Tag ? (
  <Tag
    {...rest}
    data-slot="input-group-button"
    class={cn(buttonSizeVariants({ size }), className)}
  >
    <slot />
  </Tag>
) : (
  <Button
    type={type}
    variant={variant}
    data-size={size}
    data-slot="input-group-button"
    class={cn(buttonSizeVariants({ size }), className)}
    {...(rest as any)}
  >
    <slot />
  </Button>
)}

---
import type { HTMLAttributes, HTMLTag } from "astro/types";
import { cn } from "@/lib/utils";
import { Input } from "../input";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"input">;

const {
  as: rawTag,
  class: className = "",
  ...rest
} = Astro.props as Props;

const Tag = rawTag ?? undefined;
---

{Tag ? (
  <Tag
    {...rest}
    data-slot="input-group-control"
    class={cn(
      "flex-1 rounded-none border-0 bg-transparent shadow-none focus-visible:ring-0 dark:bg-transparent",
      className,
    )}
  />
) : (
  <Input
    data-slot="input-group-control"
    class={cn(
      "flex-1 rounded-none border-0 bg-transparent shadow-none focus-visible:ring-0 dark:bg-transparent",
      className,
    )}
    {...(rest as any)}
  />
)}

