---
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"li">;

const {
  as: Tag = "li",
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<Tag
  {...rest}
  data-slot="link-group-item"
  class={cn(
    "[&_a]:transition-colors [&_a]:underline-offset-4 [&_a:hover]:underline [&_a]:text-footer-primary/60 [&_a]:text-muted-foreground",
    className,
  )}
>
  <slot />
</Tag>

export { default as LinkGroup } from "./LinkGroup.astro";
export { default as LinkGroupTitle } from "./LinkGroupTitle.astro";
export { default as LinkGroupList } from "./LinkGroupList.astro";
export { default as LinkGroupItem } from "./LinkGroupItem.astro";
