---
import type { HTMLAttributes } from "astro/types";

type Props = HTMLAttributes<"option">;

const { ...rest } = Astro.props as Props;
---

<option data-slot="native-select-option" {...rest}><slot /></option>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"optgroup">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<optgroup data-slot="native-select-optgroup" class={cn(className)} {...rest}>
  <slot />
</optgroup>

export { default as NativeSelect } from "./NativeSelect.astro";
export { default as NativeSelectOption } from "./NativeSelectOption.astro";
export { default as NativeSelectOptGroup } from "./NativeSelectOptGroup.astro";
