import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
  value?: string;
} & HTMLAttributes<"li">;

const { class: className = "", value, ...rest } = Astro.props as Props;
---

<li
  {...rest}
  data-slot="navigation-menu-item"
  data-value={value}
  class={cn("static", className)}
>
  <slot />
</li>

---
import type { HTMLAttributes } from "astro/types";
import { cva } from "class-variance-authority";
import { ChevronDown } from "@lucide/astro";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"button">;

export const navigationMenuTriggerStyle = cva(
  "px-4 py-2.5 bg-transparent border-none cursor-pointer flex items-center gap-1.5 transition-colors relative z-1 hover:text-foreground data-[state=open]:text-foreground",
);

const {
  class: className = "",
  type = "button",
  ...rest
} = Astro.props as Props;
---

<button
  {...rest}
  data-slot="navigation-menu-trigger"
  type={type}
  class={cn(navigationMenuTriggerStyle(), "group", className)}
>
  <slot />
  <ChevronDown
    class="text-muted-foreground size-3 transition-transform duration-200 ease group-data-[state=open]:rotate-180"
    aria-hidden="true"
  />
</button>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
  align?: "start" | "center" | "end";
  alignOffset?: number;
  side?: "top" | "bottom";
  sideOffset?: number;
} & HTMLAttributes<"div">;

const {
  class: className = "",
  align = "start",
  alignOffset = 0,
  side = "bottom",
  sideOffset = 4,
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  data-slot="navigation-menu-content"
  data-align={align}
  data-align-offset={alignOffset}
  data-side={side}
  data-side-offset={sideOffset}
  hidden
  class={cn(
    "transition-[opacity,translate,filter] duration-250 ease-[cubic-bezier(0.32,0.72,0,1)]",
    "data-starting-style:opacity-0 data-starting-style:pointer-events-none",
    "data-[ending-style]:opacit-0",
    "data-[motion=from-right]:animate-in data-[motion=from-right]:fade-in data-[motion=from-right]:slide-in-from-right-50",
    "data-[motion=from-left]:animate-in data-[motion=from-left]:fade-in data-[motion=from-left]:slide-in-from-left-50",
    // slide exit — transition TO these values
    "data-ending-style:animate-out data-ending-style:fade-out",
    "data-[motion=to-left]:data-[ending-style]:slide-out-to-left-50 data-[motion=to-right]:data-[ending-style]:slide-out-to-right-50",
    "absolute",
    className,
  )}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<div
  data-slot="navigation-menu-viewport-positioner"
  class={cn(
    "isolate z-50 transition-[left,top] duration-250 ease data-ending-style:opacity-0",
  )}
>
  <div
    {...rest}
    data-slot="navigation-menu-viewport"
    class={cn(
      "bg-popover shadow-lg rounded-lg outline outline-border overflow-clip",
      "w-(--viewport-width,0) h-(--viewport-height,0)",
      "origin-(--transform-origin)",
      "duration-250 ease data-instant:transition-[scale,opacity] transition-[width,height,transform,scale,opacity]",
      "data-[state=open]:opacity-100 data-[state=open]:pointer-events-auto",
      "data-instant:data-[state=open]:animate-in data-instant:data-[state=open]:fade-in data-instant:data-[state=open]:zoom-in-95",
      "data-ending-style:opacity-0 data-ending-style:scale-90",
      "will-change-[width,height,transform]",
      className,
    )}
  >
  </div>
</div>

