import type { HTMLAttributes, HTMLTag } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"a">;

const { as: Tag = "a", class: className = "", ...rest } = Astro.props as Props;
---

<Tag
  {...rest}
  data-slot="navigation-menu-link"
  class={cn(
    "data-[active=true]:focus:bg-muted data-[active=true]:hover:bg-muted data-[active=true]:bg-muted/50 data-[active=true]:text-foreground hover:bg-muted hover:text-foreground focus:bg-muted focus:text-foreground focus-visible:ring-ring/50 [&_svg:not([class*='text-'])]:text-muted-foreground flex flex-col gap-1 rounded-sm p-3 transition-all outline-none focus-visible:ring-[3px] focus-visible:outline-1 [&_svg:not([class*='size-'])]:size-4",
    className,
  )}
>
  <slot />
</Tag>

