---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";
import { Button } from "../button";

type Props = {
  class?: string;
  /** Radix-like: render wrapper so the child can be the interactive element */
  asChild?: boolean;
} & HTMLAttributes<"button">;

const { class: className = "", asChild = false, ...rest } = Astro.props as Props;
---

{
  asChild ? (
    <div data-slot="popover-trigger" data-as-child class={cn("inline-flex items-center", className)}>
      <slot />
    </div>
  ) : (
    <Button
      data-slot="popover-trigger"
      class={cn("inline-flex items-center", className)}
      {...rest as any}
    >
      <slot />
    </Button>
  )
}

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";
import { cva } from "class-variance-authority";

type Props = {
  side?: "top" | "bottom" | "left" | "right";
  align?: "start" | "center" | "end";
  alignOffset?: number;
  sideOffset?: number;
  class?: string;
} & HTMLAttributes<"div">;

const {
  side = "bottom",
  align = "center",
  sideOffset = 6,
  alignOffset = 0,
  class: className = "",
  ...rest
} = Astro.props as Props;

const contentVariants = cva([
  "z-50 duration-150 ease transition-discrete outline-hidden",
  "bg-popover text-popover-foreground ring-foreground/10 ring-1",
  "flex w-72 flex-col gap-2.5 rounded-lg p-4 text-sm shadow-md",
  "data-[state=open]:animate-in data-[state=closed]:animate-out",
  "data-[state=closed]:fade-out-0 data-[state=open]:fade-in-0",
  "data-[state=closed]:zoom-out-95 data-[state=open]:zoom-in-95",
  "data-[side=bottom]:slide-in-from-top-2 data-[side=top]:slide-in-from-bottom-2 data-[side=left]:slide-in-from-right-2 data-[side=right]:slide-in-from-left-2",
  "origin-(--transform-origin) [--tw-animation-fill-mode:both]",
]);
---

<div
  {...rest}
  hidden
  data-slot="popover-content"
  data-align={align}
  data-align-offset={alignOffset}
  data-side={side}
  data-side-offset={sideOffset}
  style={`--side-offset: ${sideOffset}px`}
  class={cn(contentVariants(), className)}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  as?: string;
  class?: string;
} & HTMLAttributes<"button">;

const { as: Tag = "button", class: className = "", ...rest } = Astro.props as Props;
---

<Tag
  {...rest}
  data-slot="popover-close"
  class={cn(className)}
>
  <slot />
</Tag>

