---
/**
 * @component RadioGroup
 * @title Radio Group
 * @description A group of options where users can select exactly one choice.
 * @figmaUrl https://www.figma.com/design/koxai7zw5vIuBzuVxe5T2l/bejamas-ui?node-id=2549-18817&t=YFStJ3V8fXEO8QD8-4
 *
 * @preview
 *
 * <RadioGroup>
 *   <div class="flex items-center gap-2">
 *     <RadioGroupItem name="plan" value="free" checked id="free" />
 *     <Label for="free">Free</Label>
 *   </div>
 *   <div class="flex items-center gap-2">
 *     <RadioGroupItem name="plan" value="pro" id="pro" />
 *     <Label for="pro">Pro</Label>
 *   </div>
 * </RadioGroup>
 *
 * @usage
 *
 * ```astro nocollapse
 * ---
 * import { RadioGroup, RadioGroupItem } from '@bejamas/ui/components/radio-group';
 * import { Label } from '@bejamas/ui/components/label';
 * ---
 *
 * <RadioGroup>
 *   <div class="flex items-center gap-2">
 *     <RadioGroupItem name="plan" value="free" id="free" checked />
 *     <Label for="free">Free</Label>
 *   </div>
 * </RadioGroup>
 * ```
 */
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
  "aria-label"?: string;
  "aria-labelledby"?: string;
} & HTMLAttributes<"div">;

const {
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  role="radiogroup"
  data-slot="radio-group"
  class={cn("grid gap-3", className)}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
  name: string;
  value: string;
  checked?: boolean;
  disabled?: boolean;
  required?: boolean;
  id?: string;
  "aria-invalid"?: boolean;
} & HTMLAttributes<"div">;

const {
  class: className = "",
  id: inputId,
  name,
  value,
  checked = false,
  disabled = false,
  required = false,
  "aria-invalid": ariaInvalid,
  ...rest
} = Astro.props as Props;
---

<div
  {...rest}
  data-slot="radio-group-item"
  class={cn("relative inline-grid place-items-center", className)}
>
  <input
    type="radio"
    id={inputId}
    name={name}
    value={value}
    checked={checked}
    disabled={disabled}
    required={required}
    aria-invalid={ariaInvalid}
    class="peer absolute left-1/2 top-1/2 size-4 -translate-x-1/2 -translate-y-1/2 opacity-0"
  />
  <span
    aria-hidden="true"
    data-slot="radio-group-control"
    class={cn(
      "border-input text-primary focus-visible:border-ring focus-visible:ring-ring/50 aria-invalid:ring-destructive/20 dark:aria-invalid:ring-destructive/40 aria-invalid:border-destructive dark:bg-input/30 aspect-square size-4 shrink-0 rounded-full border shadow-xs transition-[color,box-shadow] outline-none focus-visible:ring-[3px] disabled:cursor-not-allowed disabled:opacity-50",
      "peer-focus-visible:border-ring peer-focus-visible:ring-ring/50 peer-focus-visible:ring-[3px]",
      "peer-disabled:cursor-not-allowed peer-disabled:opacity-50",
      ariaInvalid
        ? "ring-destructive/20 dark:ring-destructive/40 border-destructive"
        : "",
      "peer-checked:[&>svg]:opacity-100",
    )}
  >
    <svg
      aria-hidden="true"
      viewBox="0 0 20 20"
      class="pointer-events-none absolute left-1/2 top-1/2 size-2 -translate-x-1/2 -translate-y-1/2 opacity-0 transition-opacity duration-150 ease-out"
    >
      <circle cx="10" cy="10" r="10" class="fill-primary" />
    </svg>
  </span>
</div>
