import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  label?: string;
  class?: string;
} & HTMLAttributes<"optgroup">;

const {
  label,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<optgroup
  {...rest}
  data-slot="select-group"
  label={label}
  class={cn("py-1", className)}
>
  <slot />
</optgroup>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"span">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<span
  {...rest}
  data-slot="select-indicator"
  aria-hidden="true"
  class={cn(
    "absolute inset-y-0 right-3 z-10 flex items-center pointer-events-none text-muted-foreground transition-transform peer-focus-visible:rotate-180",
    className,
  )}
>
  <slot>
    <svg
      aria-hidden="true"
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      stroke-width="2"
      stroke-linecap="round"
      stroke-linejoin="round"
      class="size-4"
    >
      <path d="m6 9 6 6 6-6"></path>
    </svg>
  </slot>
</span>

