import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  value?: string;
  disabled?: boolean;
  selected?: boolean;
  class?: string;
} & HTMLAttributes<"option">;

const {
  value,
  disabled = false,
  selected = false,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<option
  {...rest}
  data-slot="select-option"
  value={value}
  disabled={disabled}
  selected={selected}
  class={cn(
    "checked:text-primary/50 flex items-center gap-2 outline-none",
    "px-2 py-1.5 text-sm [&:hover]:bg-accent [&:hover]:text-accent-foreground",
    "rounded-sm cursor-default transition-discrete select-none",
    className,
  )}
>
  <slot />
</option>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"option">;

const {
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<option
  {...rest}
  data-slot="select-placeholder"
  disabled
  selected
  hidden
  value=""
  class={cn(
    "text-muted-foreground dark:text-muted-foreground",
    className,
  )}
>
  <slot />
</option>

export { default as Select } from "./Select.astro";
export { default as SelectControl } from "./SelectControl.astro";
export { default as SelectOption } from "./SelectOption.astro";
export { default as SelectGroup } from "./SelectGroup.astro";
export { default as SelectPlaceholder } from "./SelectPlaceholder.astro";
export { default as SelectIndicator } from "./SelectIndicator.astro";
