---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<div
  {...rest}
  data-slot="slider-track"
  class={cn(
    "bg-muted relative flex-1 rounded-full",
    "h-1 group-data-[orientation=vertical]/slider:h-auto group-data-[orientation=vertical]/slider:w-1",
    "group-data-[disabled]/slider:opacity-50",
    className,
  )}
>
  <slot />
</div>

---
import type { HTMLAttributes } from "astro/types";
