---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  value: string;
  disabled?: boolean;
  class?: string;
} & HTMLAttributes<"button">;

const {
  as: Tag = "button",
  value,
  disabled = false,
  class: className = "",
  ...rest
} = Astro.props as Props;

const tabsTriggerVariants = cva(
  "relative z-10 focus-visible:border-ring focus-visible:ring-ring/50 focus-visible:outline-ring text-foreground dark:text-muted-foreground inline-flex h-[calc(100%-1px)] flex-1 items-center justify-center gap-1.5 whitespace-nowrap rounded-md border border-transparent px-2 py-1 text-sm font-medium transition-[color,box-shadow] focus-visible:ring-[3px] focus-visible:outline-1 disabled:pointer-events-none disabled:opacity-50 data-[state=active]:shadow-sm [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4",
);
---

<Tag
  {...rest}
  type="button"
  role="tab"
  data-slot="tabs-trigger"
  data-value={value}
  class={cn(
    "inline-flex items-center relative z-1 justify-center rounded-md px-3 py-1.5 text-sm transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring/50",
    className,
  )}
>
  <slot />
</Tag>

export { default as Tabs } from "./Tabs.astro";
export { default as TabsList } from "./TabsList.astro";
export { default as TabsTrigger } from "./TabsTrigger.astro";
export { default as TabsContent } from "./TabsContent.astro";
export { default as TabsIndicator } from "./TabsIndicator.astro";
