---
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

export const tabsContentVariants = cva(
  "focus-visible:border-ring focus-visible:ring-ring/50 text-sm flex-1 outline-none focus-visible:ring-[3px] hidden data-[state=active]:block",
  {
    variants: {
      animation: {
        none: "",
        fade: "data-[state=active]:animate-in data-[state=active]:fade-in-0 data-[state=active]:duration-200",
        slide:
          "data-[state=active]:animate-in data-[state=active]:fade-in-0 data-[state=active]:slide-in-from-bottom-2 data-[state=active]:duration-200",
        direction:
          "data-[state=active]:animate-in data-[state=active]:duration-200 data-[activation-direction=left]:slide-in-from-left-2 data-[activation-direction=right]:slide-in-from-right-2 data-[activation-direction=up]:slide-in-from-top-2 data-[activation-direction=down]:slide-in-from-bottom-2 data-[activation-direction=left]:fade-in-0 data-[activation-direction=right]:fade-in-0 data-[activation-direction=up]:fade-in-0 data-[activation-direction=down]:fade-in-0",
      },
    },
    defaultVariants: {
      animation: "none",
    },
  },
);

type Props = {
  as?: HTMLTag;
  value: string;
  class?: string;
} & VariantProps<typeof tabsContentVariants> &
  HTMLAttributes<"div">;

const {
  as: Tag = "div",
  value,
  animation = "none",
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<Tag
  {...rest}
  role="tabpanel"
  data-slot="tabs-content"
  data-value={value}
  tabindex="0"
  class={cn(tabsContentVariants({ animation }), className)}
>
  <slot />
</Tag>

---
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

export const tabsListVariants = cva(
  "group/tabs-list text-muted-foreground relative inline-flex w-fit items-center justify-center group-data-[orientation=vertical]/tabs:h-fit group-data-[orientation=vertical]/tabs:flex-col",
  {
    variants: {
      variant: {
        indicator: "rounded-lg border border-input bg-muted p-[2px]",
        default: "rounded-lg bg-muted p-[3px]",
        line: "rounded-none bg-transparent p-0 gap-1",
        "line-animated": "rounded-none bg-transparent p-0 gap-1",
      },
      size: {
        default: "group-data-[orientation=horizontal]/tabs:h-9",
        sm: "group-data-[orientation=horizontal]/tabs:h-8",
        lg: "group-data-[orientation=horizontal]/tabs:h-10",
      },
    },
    defaultVariants: {
      variant: "indicator",
      size: "default",
    },
  },
);

type Props = {
  as?: HTMLTag;
  class?: string;
} & VariantProps<typeof tabsListVariants> &
  HTMLAttributes<"div">;

const {
  as: Tag = "div",
  variant = "indicator",
  size = "default",
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<Tag
  {...rest}
  role="tablist"
  data-slot="tabs-list"
  data-variant={variant}
  class={cn(tabsListVariants({ variant, size }), className)}
>
  {
    variant === "indicator" && (
      <div
        aria-hidden="true"
        data-slot="tabs-indicator"
        class="pointer-events-none absolute top-[var(--active-tab-top)] left-[var(--active-tab-left)] z-0 h-[var(--active-tab-height)] w-[var(--active-tab-width)] rounded-md bg-background shadow-sm transition-all duration-150 ease dark:border-input dark:bg-input/30"
      />
    )
  }
  {
    variant === "line-animated" && (
      <div
        aria-hidden="true"
        data-slot="tabs-indicator"
        class="pointer-events-none absolute z-0 rounded-full bg-foreground transition-all duration-150 ease group-data-[orientation=horizontal]/tabs:left-[var(--active-tab-left)] group-data-[orientation=horizontal]/tabs:top-[calc(var(--active-tab-top)+var(--active-tab-height)-2px)] group-data-[orientation=horizontal]/tabs:h-0.5 group-data-[orientation=horizontal]/tabs:w-[var(--active-tab-width)] group-data-[orientation=vertical]/tabs:top-[var(--active-tab-top)] group-data-[orientation=vertical]/tabs:left-[calc(var(--active-tab-left)+var(--active-tab-width)-2px)] group-data-[orientation=vertical]/tabs:h-[var(--active-tab-height)] group-data-[orientation=vertical]/tabs:w-0.5"
      />
    )
  }
  <slot />
</Tag>

---
import { cn } from "@/lib/utils";
import type { HTMLAttributes } from "astro/types";

type Props = {
  value: string;
  disabled?: boolean;
  class?: string;
} & HTMLAttributes<"button">;

const {
  value,
  disabled = false,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<button
  {...rest}
  type="button"
  disabled={disabled}
  role="tab"
  data-slot="tabs-trigger"
  data-value={value}
  class={cn(
    "focus-visible:border-ring focus-visible:ring-ring/50 relative z-10 inline-flex h-[calc(100%-1px)] flex-1 items-center justify-center gap-1.5 whitespace-nowrap rounded-md border border-transparent px-1.5 py-0.5 text-sm font-medium transition-all outline-none focus-visible:ring-[3px] disabled:pointer-events-none disabled:opacity-50 aria-disabled:pointer-events-none aria-disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4 group-data-[orientation=vertical]/tabs:w-full group-data-[orientation=vertical]/tabs:justify-start",
    "text-foreground/60 hover:text-foreground dark:text-muted-foreground dark:hover:text-foreground data-[state=active]:text-foreground",
    "group-data-[variant=default]/tabs-list:data-[state=active]:bg-background group-data-[variant=default]/tabs-list:data-[state=active]:shadow-sm dark:group-data-[variant=default]/tabs-list:data-[state=active]:border-input dark:group-data-[variant=default]/tabs-list:data-[state=active]:bg-input/30",
    "group-data-[variant=indicator]/tabs-list:data-[state=active]:bg-transparent group-data-[variant=indicator]/tabs-list:data-[state=active]:shadow-none",
    "group-data-[variant=line]/tabs-list:rounded-none group-data-[variant=line]/tabs-list:bg-transparent group-data-[variant=line]/tabs-list:data-[state=active]:bg-transparent group-data-[variant=line]/tabs-list:data-[state=active]:shadow-none",
    "group-data-[variant=line-animated]/tabs-list:rounded-none group-data-[variant=line-animated]/tabs-list:bg-transparent group-data-[variant=line-animated]/tabs-list:data-[state=active]:bg-transparent group-data-[variant=line-animated]/tabs-list:data-[state=active]:shadow-none",
    "after:pointer-events-none after:absolute after:bg-foreground after:opacity-0 after:transition-opacity group-data-[orientation=horizontal]/tabs:after:inset-x-0 group-data-[orientation=horizontal]/tabs:after:bottom-[-5px] group-data-[orientation=horizontal]/tabs:after:h-0.5 group-data-[orientation=vertical]/tabs:after:inset-y-0 group-data-[orientation=vertical]/tabs:after:-right-1 group-data-[orientation=vertical]/tabs:after:w-0.5 group-data-[variant=default]/tabs-list:after:hidden group-data-[variant=indicator]/tabs-list:after:hidden group-data-[variant=line-animated]/tabs-list:after:hidden group-data-[variant=line]/tabs-list:data-[state=active]:after:opacity-100",
    className,
  )}
>
  <slot />
</button>

export { default as Tabs } from "./Tabs.astro";
export { default as TabsList } from "./TabsList.astro";
export { default as TabsTrigger } from "./TabsTrigger.astro";
export { default as TabsContent } from "./TabsContent.astro";
export { tabsListVariants } from "./TabsList.astro";
export { tabsContentVariants } from "./TabsContent.astro";
