---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  value: string;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  value,
  class: className = "",
  ...rest
} = Astro.props as Props;

const tabsContentVariants = cva(
  "focus-visible:border-ring focus-visible:ring-ring/50 flex-1 outline-none focus-visible:ring-[3px] hidden data-[state=active]:block",
);
---

<Tag
  {...rest}
  role="tabpanel"
  data-slot="tabs-content"
  data-value={value}
  tabindex="0"
  class={cn(tabsContentVariants(), className)}
>
  <slot />
</Tag>

---
import type { HTMLAttributes } from "astro/types";
import { cn } from "@/lib/utils";

type Props = {
  class?: string;
} & HTMLAttributes<"div">;

const { class: className = "", ...rest } = Astro.props as Props;
---

<div
  data-slot="tabs-indicator"
  class={cn(
    "indicator bg-background shadow-sm translate-x-[var(--active-tab-left)] left-0 rounded w-[var(--active-tab-width)] absolute h-[calc(100%-var(--spacing)*2)] transition-all duration-300 ease-in-out",
    className,
  )}
  {...rest}
>
  <slot />
</div>

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  as?: HTMLTag;
  class?: string;
} & HTMLAttributes<"div">;

const {
  as: Tag = "div",
  class: className = "",
  ...rest
} = Astro.props as Props;

const tabsListVariants = cva(
  "bg-secondary text-secondary-foreground relative inline-flex h-12 w-fit items-center rounded-md p-1",
);
---

<Tag
  {...rest}
  role="tablist"
  data-slot="tabs-list"
  class={cn(tabsListVariants(), className)}
>
  <slot />
</Tag>

---
import { cva } from "class-variance-authority";
import { cn } from "@/lib/utils";
import type { HTMLAttributes, HTMLTag } from "astro/types";

type Props = {
  value: string;
  disabled?: boolean;
  class?: string;
} & HTMLAttributes<"button">;

const {
  value,
  disabled = false,
  class: className = "",
  ...rest
} = Astro.props as Props;
---

<button
  {...rest}
  type="button"
  role="tab"
  data-slot="tabs-trigger"
  data-value={value}
  class={cn(
    "inline-flex items-center relative z-1 justify-center rounded-md px-4 py-2 transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring/50",
    className,
  )}
>
  <slot />
</button>

export { default as Tabs } from "./Tabs.astro";
export { default as TabsList } from "./TabsList.astro";
export { default as TabsTrigger } from "./TabsTrigger.astro";
export { default as TabsContent } from "./TabsContent.astro";
export { default as TabsIndicator } from "./TabsIndicator.astro";
